import { StyleSheet, View, TouchableOpacity, Text, ScrollView } from 'react-native'
import data from '../services/clothes-model.json'
import React, {useState} from 'react';

export default function Categories({clothes}) {
  const [count, setCount] = useState(0);
  const onPress = () => setCount(prevCount => prevCount + 1);
  const priceVariable=()=>{
    if(clothes.VARIABLE==0){
      return(<Text>{clothes.PRICE}</Text>);
    }
    else{
      return(<Text>price is variable</Text>);
    }

  }

    
  return (
      <TouchableOpacity style={styles.button} onPress={onPress}>
        <Text>{clothes.ID}</Text>
        <Text>{clothes.NAME}</Text>
        <Text>{priceVariable}</Text>
      </TouchableOpacity>
            
           
  )
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingHorizontal: 10,
  },
  button: {
    alignItems: 'center',
    backgroundColor: '#DDDDDD',
    padding: 10,
  },
  countContainer: {
    alignItems: 'center',
    padding: 10,
  },
});
