import React from 'react';
import {Text, Image, View, StyleSheet, TouchableOpacity, ScrollView, FlatList} from 'react-native';
// import  data from '../services/TEST.json'
const data = [
  { ID: "1A1", name: "Hussain" },
  { ID: "1A2", name: "Sabrina" },
];

const renderItem = ({ item }) => {
  //the app will represent each list item via a Text component
  return <Text> {item.ID}</Text>;
};

export function Categories() {
  return (
    <FlatList dataList={data}
      keyExtractor={(data) => data.name}
      renderItem={renderItem}/>
  );
}
const styles = StyleSheet.create({
  card: {
    backgroundColor: 'white',
    borderRadius: 16,
    shadowOpacity: 0.2,
    shadowRadius: 4,
    shadowColor: 'black',
    shadowOffset: {
      height: 0,
      width: 0,
    },
    elevation: 1,
    marginVertical: 20,
  },
  thumb: {
    height: 260,
    borderTopLeftRadius: 16,
    borderTopRightRadius: 16,
    width: '100%',
  },
  infoContainer: {
    padding: 16,
  },
  name: {
    fontSize: 22,
    fontWeight: 'bold',
  },
  price: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 8,
  },
});
//use CSS to decorate our list
const styles1 = StyleSheet.create({
  item: {
    backgroundColor: "blue",
    color: "white",
    padding: 2,
    margin: 2,
  },
});
